rss.onEnter:
cachePath = getStoragePath("key");
optionsPath = cachePath + "onehd.dat";
optionsArray = readStringFromFile(optionsPath);
if(optionsArray == null)
{
  buf = "60000";
}
else
{
  buf = getStringArrayAt(optionsArray, 0);
}
startitem = "middle";
setRefreshTime(1);

rss.onExit:
arr = null;
arr = pushBackStringArray(arr, buf);
print("arr=",arr);

writeStringToFile(optionsPath, arr);

text.script:
idx = getQueryItemIndex();
focus = getFocusItemIndex();
if(focus==idx) 
{
  location = getItemInfo(idx, "location");
  annotation = getItemInfo(idx, "annotation");
}
getItemInfo(idx, "title");

fontSize.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "16"; else "14";

backgroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "10:80:120"; else "-1:-1:-1";

foregroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "255:255:255"; else "140:140:140";

onUserInput.script:
ret = "false";
userInput = currentUserInput();

if (userInput == "pagedown" || userInput == "pageup")
{
  idx = Integer(getFocusItemIndex());
  if (userInput == "pagedown")
  {
    idx -= -8;
    if(idx >= itemCount)
      idx = itemCount-1;
  }
  else
  {
    idx -= 8;
    if(idx < 0)
      idx = 0;
  }

  print("new idx: "+idx);
  setFocusItemIndex(idx);
	setItemFocus(0);
  "true";
}
else if (userInput == "one" || userInput == "1")
{
		if (buf == "60000")
           buf = "40000";
		else if (buf == "40000")
           buf = "20000";
        else if (buf == "20000")
          buf = "10000";
        else if (buf == "10000")
          buf = "8000";
        else if (buf == "8000")
          buf = "6000";
        else if (buf == "6000")
          buf = "4000";
        else if (buf == "4000")
          buf = "2000";
        else if (buf == "2000")
          buf = "1000";
        else if (buf == "1000")
          buf = "500";
        else if (buf == "500")
          buf = "400";
        else if (buf == "400")
          buf = "300";
        else if (buf == "300")
          buf = "200";
        else if (buf == "200")
          buf = "100";
        else if (buf == "100")
          buf = "0";
        else if (buf == "0")
          buf = "60000";
        else
		 buf = "60000";
  arr = null;
  arr = pushBackStringArray(arr, buf);
  print("arr=",arr);

  writeStringToFile(optionsPath, arr);
  ret = "true";
}
redrawDisplay();
ret;
