}

onUserInput.script:
      ret = "false";
      
      userInput = currentUserInput();
      majorContext = getPageInfo("majorContext");
      
      print("*** majorContext=",majorContext);
      print("*** userInput=",userInput);
      
      idx = Integer(getFocusItemIndex());

      if (majorContext == "items" && idx < 2)
      {
        if( userInput == "right" || userInput == "enter" || userInput == "ENTR" )
        {
          if(idx == 0)
          {
            print("username!!!!");
    				keyword = getInput("Input", "doModal");
    				if (keyword != null)
    				{
    				  username = keyword;
    				}
          }
          else if(idx == 1)
          {
            print("pass!!!!");
    				keyword = getInput("Input", "doModal");
    				if (keyword != null)
    				{
    				  pass = keyword;
    				}
          }
          ret = "true";
          redrawDisplay();
        }
      }
      ret;

rss.onExit:

if(username == "" || username == null || pass == "" || pass == null)
{
  username = " ";
  pass = " ";
}
else
{
serialepe=username + "|" + pass;
writeStringToFile(optionsPath, serialepe);
}

rss.script:
	optionsPath = "/usr/local/etc/dvdplayer/sovok.txt";
  pageTitle = "Setari user si parola pentru sovok.tv";
  setari = readStringFromFile(optionsPath);
	itemTitleArray = null;
	topUrl="/usr/local/etc/www/cgi-bin/scripts/tv/php/sovok.rss";
	dlok = loadXMLFile(topUrl);
	if (dlok != null)
	{
		itemSize = getXMLElementCount("rss", "channel", "item");
		print("itemSize = ", itemSize);

		if(itemSize > 0)
		{
			itemCount = 0;
      	
			while(1)
			{
				itemTitle  = getXMLText("rss", "channel", "item", itemCount, "title");
				print("itemTitle = ", itemTitle);
				itemTitleArray  = pushBackStringArray(itemTitleArray, itemTitle);
      	
				itemCount += 1;
				if(itemCount == itemSize)
				{
					break;
				}
			}
			setFocusItemIndex(0);
			setItemFocus(0);
			redrawDisplay();
		}
	}
